import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import ProductService from "../service/product_service"
const Home = () => {


    const [productList, setproductList] = useState([]);
    const [msg, setMsg] = useState("");


    useEffect(() => {
       init()
    }, []);


    const init=()=>{
        ProductService.getAllProducts().then((res) => {
            setproductList(res.data)
        }).catch((err) => {
            console.log("Unable to Fetch the Products");
        })
    }

    const deleteProduct = (id) => {
        ProductService.deleteProduct(id).then((res) => {
            setMsg("Deleted Product")
        }).catch((err) => {
            console.log("Unable to Delete the product");
        })
    }



    return (
        <>
            <div className="container mt-3">
                <div className="row">
                    <div className="col-md-12">
                        <div className="card">
                            <div className="card-header fs-3 text-center">
                                All Product List
                            </div>
                            {msg && <p className="fs-4 text-center text-success">{msg}</p>}

                            <div className="card-body">
                                <table class="table">
                                    <thead>
                                        <tr>
                                            <th scope="col">S.no</th>
                                            <th scope="col">Product Name</th>
                                            <th scope="col">Description</th>
                                            <th scope="col">Price</th>
                                            <th scope="col">Status</th>
                                            <th scope="col">Action</th>
                                        </tr>
                                    </thead>
                                    <tbody>
                                        {productList.map((p, num) => (
                                            <tr>
                                                <td>{num + 1}</td>
                                                <td>{p.productName}</td>
                                                <td>{p.description}</td>
                                                <td>{p.price}</td>
                                                <td>{p.status}</td>
                                                <td>
                                                    <Link to="" className="btn btn-sm btn-primary">Edit</Link>
                                                    <button onClick={()=>deleteProduct(p.id)} className="btn btn-sm btn-danger ms-1">Delete</button>

                                                </td>

                                            </tr>
                                        ))}
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )
}
export default Home
